-- Table `loker` --
CREATE TABLE `loker` (
  `idLoker` int(11) NOT NULL AUTO_INCREMENT,
  `judulLoker` varchar(255) NOT NULL,
  `deskripsi` text NOT NULL,
  `closingDate` date NOT NULL,
  `tanggalUpload` date NOT NULL,
  `tanggalEdit` date DEFAULT NULL,
  `idAdmin` int(11) NOT NULL,
  PRIMARY KEY (`idLoker`),
