  CONSTRAINT `loker_ibfk_1` FOREIGN KEY (`idAdmin`) REFERENCES `admin` (`idAdmin`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=latin1;

INSERT INTO `loker` (`idLoker`, `judulLoker`, `deskripsi`, `closingDate`, `tanggalUpload`, `tanggalEdit`, `idAdmin`) VALUES
(1, 'Loker di PT Kao Indonesia 2017', '<h4> <font size=\"4\">Management Trainee</font></h4> <i>Persyaratan:</i><br> <ul><li>Pendidikan lulusan S1</li><li>Berasal dari jurusan:</li><ul><li>Industri</li><li>Teknik Kimia</li><li>Kimia</li><li>Ekonomi</li><li>Studi Bisnis</li><li>Administrasi</li><li>Manajemen</li><li>Pemasaran</li><li>Farmasi</li></ul><li>Fresh graduate dipersilahkan untuk melamar</li><li>Memiliki kepribadian yang menyenangkan, keterampilan komunikasi yang baik, berorientasi pada target, inisiatif diri, berpikir analitis yang kuat dan kepemimpinan yang kuat</li><li>Fasih berbahasa Inggris dan familiar menggunakan program Ms. Office (Word, Excel, Power point)</li><li>Bersedia untuk diproses dan ditempatkan di Jakarta Selatan</li><li>Bekerja full time</li></ul> <br> Bagi anda para pencari kerja yang tertarik untuk berkarir bersama PT Kao Indonesia dan memenuhi segala kebutuhan yang dipersyaratkan pada <b><a href=\"http://www.openkerja.com/2014/11/lowongan-kerja-pt-kao-indonesia.html\" target=\"_blank\">Lowongan Kerja PT Kao Indonesia Terbaru</a></b> bulan Maret 2017 diatas, maka silahkan siapkan berkas lamaran kerja lengkap seperti surat lamaran kerja, CV, foto terbaru serta dokumen pelengkap atau pendukung lainnya.<br> <br> Adapun tata cara yang telah ditetapkan dalam proses rekrutmen ini yaitu dengan melakukan pendaftaran dan penyampaian dokumen kelengkapan administrasi permohonan dilakukan dengan mengirimkan aplikasi lamaran lengkap maks. 200 kb paling lambat 12 Maret 2017&nbsp;via email ke:<br> <div style=\"text-align: center;\"> <br> <b>hrinfo@kao.co.id</b><br> dengan subject:<b> MT KAO 2017 - UNPAR<br><br></b><div align=\"left\">Catatan: <ul><li>Bersedia ditempatkan di jobsite perusahaan di seluruh Indonesia</li><li>Hanya kandidat terbaik yang memenuhi kualifikasi yang akan diproses</li><li>Semua tahapan seleksi Lowongan Kerja PT Kao Indonesia tidak dipungut biaya alias gratis</li><li>Hati-hati terhadap berbagai penipuan lowongan kerja yang sering terjadi</li><li>Follow akun Instagram <a href=\"https://www.instagram.com/openkerja/\" rel=\"nofollow\" target=\"_blank\">@openkerja</a></li><li>Semoga Informasi Lowongan Kerja Terbaru ini sangat membantu para pencari kerja</li></ul></div></div>', '2017-03-12', '2017-03-04', '2017-03-05', 1),
(3, 'Lowongan Kerja di Bank BCA 2017', '<h4> Program Relationship Officer (PRO)</h4> Persyaratan :<br> <ul><li>Pendidikan Lulusan Sarjana (<a href=\"http://www.openkerja.com/search/label/S1\" target=\"_blank\">S1</a>)</li><li>IPK minimal 2,75 (skala 4,00)</li><li>Usia maksimal 25 tahun</li><li>Memiliki kemampuan interpersonal yang baik</li><li>Menyukai pekerjaan yang berhubungan dengan nasabah</li><li>Berpenampilan menarik dengan tinggi dan berat badan yang proporsional&nbsp;</li><li>Bersedia tidak menikah selama 1 (satu) tahun pendidikan</li><li>Bersedia menjalani ikatan dinas setelah pendidikan</li><li>Penempatan di Kantor Cabang BCA (Jakarta, Surabaya, Bandung, Pangkal Pinang, Palangkaraya, Balikpapan, Samarinda, Tarakan, Ambon, Denpasar, Batam, Lhokseumawe, Tanjung Pinang, Tebing Tinggi)</li></ul> <br> <br> <h4> Program Account Officer (PAO)</h4> Persyaratan :<br> <ul><li>Pendidikan minimal S1 dari <a href=\"http://www.openkerja.com/search/label/Semua%20Jurusan\" target=\"_blank\">Semua jurusan</a></li><li>Indeks Prestasi Kumulatif (IPK) minimal 2.75 dari skala 4.00</li><li>Usia maksimal 25 tahun</li><li>Memiliki kemampuan analisa yang baik</li><li>Memiliki kemampuan interpersonal yang baik</li><li>Memiliki keinginan berprestasi</li><li>Belum menikah dan bersedia untuk tidak menikah selama pendidikan</li><li>Bersedia menjalani ikatan dinas setelah pendidikan</li><li>Penempatan di Kantor Cabang (Jakarta, Surabaya, Bandung, Pangkal Pinang, Palangkaraya, Palembang, Banjarmasin, Pontianak, Balikpapan, Tarakan, Samarinda, Kupang, Medan, Batam, Bukit Barisanm, Kisaran, Tanjung Pinang)</li></ul> <br> <br> <h4> Program Junior Business Analyst (IT)</h4> Persyaratan :<br> <ul><li>Pendidikan Lulusan S1</li><li>IPK minimal 3.00 (skala 4.00)</li><li>Memiliki latar belakang pendidikan Sistem Informasi / Teknik Informatika / Komputerisasi <a href=\"http://www.openkerja.com/search/label/Akuntansi\" target=\"_blank\">Akuntansi</a> / Teknik Industri</li><li>Usia maksimal 25 tahun</li><li>Memiliki minat di Pengembangan Sistem atau Produk</li><li>Penempatan di Kantor Pusat (Jakarta)</li><li>Bersedia tidak menikah selama 1 (satu) tahun pendidikan</li><li>Bersedia menjalani ikatan dinas setelah pendidikan</li></ul> <br> <br> <h4> Program Analis Kredit</h4> Persyaratan :<br> <ul><li>Lulusan Sarjana (S1) dengan IPK minimal 2,75 (skala 4,00)</li><li>Memiliki latar belakang pendidikan Ekonomi atau Teknik</li><li>Usia maksimal 25 tahun</li><li>Memiliki kemampuan analisa yang baik</li><li>Memiliki keinginan berprestasi</li><li>Bersedia tidak menikah selama 1 (satu) tahun pendidikan</li><li>Bersedia menjalani ikatan dinas setelah pendidikan</li><li>Penempatan di Jakarta atau Surabaya</li></ul> <br> <br> <h4> BCA IT Talent</h4> Persyaratan :<br> <ul><li>Lulusan S1 dengan IPK minimal 3,00 (Strata 1) dan 3,25 (Strata 2)</li><li>Memiliki latar belakang pendidikan atau pekerjaan di bidang komputer</li><li>Usia maks. 25 tahun (Strata 1) dan 27 tahun (Strata 2)</li><li>Bersedia tidak menikah selama 1 (satu) tahun pendidikan</li><li>Bersedia menjalani ikatan dinas setelah pendidikan</li><li>Penempatan di Kantor Pusat (Jakarta)</li></ul> <br> <br> <h4> BCA Banker Program</h4> Persyaratan:<br> <ul><li>Pendidikan&nbsp;Lulusan S1 atau <a href=\"http://www.openkerja.com/search/label/S2\" target=\"_blank\">S2</a></li><li>Berasal dari universitas yang terkemuka di dalam ataupun luar negeri</li><li>Bagi Lulusan baru :&nbsp;</li><ul><li>IPK minimal</li><ul><li>3.50 (S1)</li><li>3.60 (S2)</li></ul><li>Berprestasi &amp; aktif organisasi (akademis/non akademis)</li></ul><li>Bagi yang Berpengalaman :&nbsp;</li><ul><li>Pengalaman maksimal 2 tahun</li><li>IPK minimal&nbsp;</li><ul><li>3.25 (S1)</li><li>3.50 (S2)</li></ul></ul><li>Usia maksimal</li><ul><li>25 tahun (S1)</li><li>27 tahun (S2)</li></ul><li>Belum menikah</li><li>Mempunyai minat yang tinggi untuk mengembangkan diri di dunia Perbankan</li><li>Proaktif dan memiliki inisiatif yang tinggi</li><li>Mempunyai dorongan untuk selalu meningkatkan diri</li><li>Menyukai tantangan</li></ul> <br> <br> <h4> Sekretaris</h4> Persyaratan :<br> <ul><li>Pendidikan minimal <a href=\"http://www.openkerja.com/search/label/D3\" target=\"_blank\">D3</a> (sedang melanjutkan S1) dari jurusan Sekretaris / Ilmu Komunikasi / Public Relation / Administrasi Niaga</li><li>Indeks Prestasi Kumulatif (IPK) minimal 2.75 dari skala 4.00</li><li>Wanita dengan usia maksimal 27 tahun</li><li>Memiliki kemampuan berbahasa Inggris merupakan nilai tambah</li><li>Menguasai Microsoft Office</li><li>Berpenampilan menarik</li><li>Ramah dan mampu berkomunikasi dengan baik</li><li>Penempatan di Jabodetabek (Kantor Cabang &amp; Kantor Pusat)</li></ul> <br> <br> <h4> Staf Mekatronik Engineer</h4> Persyaratan :<br> <ul><li>Pendidikan Lulusan Sarjana (S1)</li><li>Berasal dari jurusan IT/Robotic/Mekanikal Engineer</li><li>IPK minimal 3,00 (skala 4,00)</li><li>Memiliki pengalaman kerja selama 1 - 5 tahun dalam pabrik yang menggunakan system automation</li><li>Pria, usia maksimal 30 tahun</li><li>Mampu berbahasa Inggris secara aktif dan pasif.</li><li>Menguasai programming PLC, mekatronika.</li><li>Menguasai maintenance dasar panel listrik, motor mesin (i.e. konveyor, ASRS)</li><li>Penempatan di Serpong, <a href=\"http://www.openkerja.com/search/label/Tangerang\" target=\"_blank\">Tangerang</a></li></ul> <br> <br> <h4> Staf Legal Korporasi</h4> Persyaratan:<br> <ul><li>Minimal lulusan S1 dari Jurusan Hukum dari universitas terkemuka</li><li>IPK minimal 3.00 (skala 4.00)</li><li>Usia maksimal 27 tahun</li><li>Freshgraduates atau berpengalaman 1-2 tahun</li><li>Memiliki minat pada bidang hukum di dunia perbankan</li><li>Penempatan kantor pusat (Jakarta)</li><li>Bersedia menjalani masa kontrak selama 1 tahun dan apabila berkinerja baik selama masa kontrak akan dilakukan pengangkatan sebagai karyawan tetap</li></ul> <br> <br> <h4> Staf Keuangan</h4> Persyaratan:<br> <ul><li>Pendidikan minimal lulusan S1</li><li>Berasal dari Jurusan Akuntansi</li><li>IPK minimal 3.00 (skala 4.00)</li><li>Usia maksimal 27 tahun 0 bulan</li><li>Terbuka untuk lulusan baru ataupun pengalaman maksimal 3 tahun</li><li>Memiliki kemampuan menggunakan program komputer untuk pengolahan data keuangan/akuntansi (mis. Microsoft Excel - Advance, dll)</li><li>Pria lebih diutamakan</li><li>Penempatan kantor pusat (<a href=\"http://www.openkerja.com/search/label/Jakarta\" target=\"_blank\">Jakarta</a>)</li></ul> <br> <br> <h4> Customer Service Video Banking</h4> Persyaratan :<br> <ul><li>Pria / Wanita berpenampilan menarik&nbsp;</li><li>Lulusan Sarjana (S1) dari segala jurusan dengan IPK minimal 2,75 (skala 4,00)&nbsp;</li><li>Usia maksimal 25 tahun</li><li>Mampu berkomunikasi secara efektif baik lisan maupun tulisan.</li><li>Mampu menganalisa masalah dan teliti.</li><li>Mempunyai Talenta (Kemampuan Berbahasa Asing yg baik (English, German, Mandarin,Korean, etc)</li><li>Mampu mengoperasikan komputer dengan baik</li><li>Memiliki kemampuan analisa dan komunikasi yang baik</li><li>Jujur dan memiliki integritas yang tinggi</li><li>Bersedia bekerja shifting&nbsp;</li><li>Memiliki pengalaman bekerja di bidang marketing perbankan merupakan suatu nilai tambah</li></ul> <br> <br> <h4> Staf IT</h4> Persyaratan :<br> <ul><li>Wanita/Pria maksimal usia 27 tahun</li><li>Minimum Lulusan S1 Teknik, memiliki latar belakang pendidikan atau pekerjaan di bidang komputer</li><li>IPK minimal 2,75 (skala 4,00)</li><li>Disiplin, jujur, teliti memiliki motivasi kerja yang tinggi.</li><li>Mampu bekerja sama dalam tim</li><li>Penempatan di Jakarta / Surabaya</li></ul> <br> <h4> Staf Audit IT</h4> Persyaratan :<br> <ul><li>Wanita/Pria maksimal usia 27 tahun</li><li>Minimum Lulusan S1/S2 Ilmu Komputer/Teknik Informatika/ Sistem Informasi yang sesuai dengan minimum IPK3,00 &nbsp;(skala 4,00)</li><li>Memiliki pengalaman kerja minimal 2 tahun di bidang auditor terutama audit IT.</li><li>Disiplin, jujur, teliti memiliki motivasi kerja yang tinggi.</li><li>Mampu bekerja sama dalam tim.</li><li>Penempatan di Kantor Pusat Jakarta.</li></ul> <br> <br> <h4> Staf Pengembangan Produk</h4> Persyaratan:<br> <ul><li>Pria/Wanita dengan minimal pendidikan S1</li><li>Latar belakang pendidikan dari Teknik Informatika</li><li>IPK minimal 2.75 dari skala 4.00</li><li>Maksimal usia 27 tahun</li><li>Memiliki pengalaman di bidang pengembangan produk merupakan nilai tambah</li><li>Memiliki minat di dunia Bisnis, Kreatif dan Inovatif</li></ul> <br> <br> <h4> Staf Senior Management Information System</h4> Persyaratan:<br> <ul><li>Pendidikan minimal S1 dari jurusan Teknik Informatika / Komputer</li><li>Indeks Prestasi Kumulatif (IPK) minimal 2.75 dari skala 4.00</li><li>Usia maksimal 30 tahun</li><li>Memiliki pengalaman atau minat dalam hal data mining, data analyst dan Reporting</li><li>Menguasai reporting tools (SQL Server Reporting Services (SSRS) &amp; SSIS)</li><li>Menguasai tools business intelligent Qliksense / Qlikview</li><li>Penempatan di Jakarta</li></ul> <br> <br> <h4> Magang Bakti</h4> Persyaratan :<br> <ul><li>Warga Negara Indonesia</li><li>Pria / Wanita berpenampilan menarik</li><li>Pendidikan Lulusan <a href=\"http://www.openkerja.com/p/lowongan-kerja-sma-smk.html\" target=\"_blank\">SMA / SMK</a> (nilai rata-rata rapor semester 5 &amp; 6 min. 7,0)</li><li>Pendidikan D1 - D3 hingga S1 (IPK min. 2,50)</li><li>Usia 18 s/d 25 tahun</li><li>Sehat jasmani dan rohani</li><li>Ramah dan mampu berkomunikasi dengan baik</li><li>Tidak pernah terlibat Narkoba dan pelanggaran hukum lainnya</li><li>Bersedia ditempatkan di seluruh cabang BCA di wilayah kota seleksi</li><li>Lulus seleksi</li><li>Bersedia tidak menikah selama proses <a href=\"http://www.openkerja.com/search/label/Magang\" target=\"_blank\">magang</a></li></ul> <br> Bagi anda para pencari kerja yang tertarik untuk berkarir bersama Bank BCA dan memenuhi segala kebutuhan yang dipersyaratkan pada <b><a href=\"http://www.openkerja.com/2014/11/lowongan-kerja-bank-central-asia-bca.html\" target=\"_blank\">Lowongan Kerja Bank BCA Terbaru</a></b> bulan Februari 2017 diatas, maka silahkan siapkan berkas lamaran kerja lengkap seperti surat lamaran kerja, CV, foto terbaru serta dokumen pelengkap atau pendukung lainnya.', '2017-02-28', '2017-03-03', '2017-03-04', 1),
(4, 'Lowongan Kerja BUMN di BPJS Ketenagakerjaan 2017', '<h4 style=\"margin: 0px 0px 0.6em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 16px; line-height: normal; font-family: Roboto, Arial, sans-serif; vertical-align: baseline; text-transform: uppercase; color: rgb(68, 68, 68); background-color: rgb(255, 255, 255);\">&nbsp;PENATA MADYA SDM (SDM)</h4><span style=\"color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\">Tugas dan Tanggung Jawab</span><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\">Melakukan kegiatan pengelolaan Sumber Daya Manusia terkait :</span><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><ul style=\"margin: 0.5em 0em 0.5em 2em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 16px; line-height: 1.4em; font-family: Roboto, Arial, sans-serif; vertical-align: baseline; list-style-position: outside; list-style-image: initial; color: rgb(68, 68, 68); background-color: rgb(255, 255, 255);\"><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Penilaian Kinerja</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Training &amp; Development</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Pengembangan Karir</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Remunerasi</li></ul><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\">Persyaratan</span><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><ul style=\"margin: 0.5em 0em 0.5em 2em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 16px; line-height: 1.4em; font-family: Roboto, Arial, sans-serif; vertical-align: baseline; list-style-position: outside; list-style-image: initial; color: rgb(68, 68, 68); background-color: rgb(255, 255, 255);\"><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Pendidikan minimal S1</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Diutamakan dari program studi Manajemen SDM, Hukum, atau Psikologi</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Berasal dari perguruan tinggi yang terakreditasi minimal B</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">IPK minimal 2.75 Skala 4.00</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Usia : Belum berulang tahun yang ke 27 pada tanggal 31 Desember 2017</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Belum menikah</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Bukan merupakan Anak, Saudara Kandung, Saudara Ipar dari Karyawan Aktif BPJS Ketenagakerjaan</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Mahir dalam mengoperasikan aplikasi komputer terutama MS Office dan MS Excel</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Memiliki kemampuan komunikasi yang baik</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Memiliki interpersonal skill yang baik</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Pengalaman kerja pada job title sejenis merupakan nilai tambah</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Memiliki SIM A dan C yang masih berlaku merupakan nilai tambah</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Bersedia ditempatkan di seluruh wilayah kerja BPJS Ketenagakerjaan</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Bersedia menjalani ikatan kerja selama 2 tahun masa kerja pertama</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Bersedia tidak menikah selama 1 tahun pertama masa ikatan kerja</li></ul><ol style=\"margin: 0.5em 0em 0.5em 2em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Arial, sans-serif; vertical-align: baseline; list-style-position: outside; list-style-image: initial; color: rgb(68, 68, 68); background-color: rgb(255, 255, 255);\"></ol><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><h4 style=\"margin: 0px 0px 0.6em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 16px; line-height: normal; font-family: Roboto, Arial, sans-serif; vertical-align: baseline; text-transform: uppercase; color: rgb(68, 68, 68); background-color: rgb(255, 255, 255);\">&nbsp;PENGAWAS DAN PEMERIKSA (WAS)</h4><span style=\"color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\">Tugas dan Tanggung Jawab</span><br style=\"margin: 0px; padding: 0px; color: rgb(68, 68, 68); font-family: Roboto, Arial, sans-serif; font-size: 16px; background-color: rgb(255, 255, 255);\"><ul style=\"margin: 0.5em 0em 0.5em 2em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 16px; line-height: 1.4em; font-family: Roboto, Arial, sans-serif; vertical-align: baseline; list-style-position: outside; list-style-image: initial; color: rgb(68, 68, 68); background-color: rgb(255, 255, 255);\"><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Menyusun program kerja dan strategi Pengawasan dan Pemeriksaan.</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Melakukan sosialiasi pengawasan dan pemeriksaan atas kepatuhan dalam penyelenggaraan program Jaminan Sosial Ketenagakerjaan kepada Peserta, Calon Peserta dan Stakeholders.</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Melakukan kegiatan proses pengawasan dan pemeriksaan untuk menjamin kepatuhan Perusahaan dalam mendaftarkan pekerjanya ke dalam program Jaminan Sosial Ketenagakerjaan.</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Membangun dan membina hubungan kerjasama dengan Pemerintah Daerah dan Instansi Penegak Hukum dalam kaitannya dengan kegiatan pengawasan dan pemeriksaan serta pengenaan sanksi terhadap Perusahaan.</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Memberikan sanksi berupa sanksi teguran, denda dan menerbitkan usulan sanksi administratif yang diatur dalam peraturan perundang.</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Melakukan verifikasi dan validasi atas data ketidakpatuhan Peserta dan Calon Peserta.</li><li style=\"margin: 0.5em 0px; padding: 0px; border: 0px; font-style: inherit; font-