-- Table `profil` --
CREATE TABLE `profil` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `idAdmin` int(11) DEFAULT NULL,
  `profilDeskripsi` text,
  `last_updated` date DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idAdmin` (`idAdmin`),
  CONSTRAINT `profil_ibfk_1` FOREIGN KEY (`idAdmin`) REFERENCES `admin` (`idAdmin`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=latin1;

INSERT INTO `profil` (`id`, `idAdmin`, `profilDeskripsi`, `last_updated`) VALUES
(1, 1, '<div style=\"text-align: center;\"><span style=\"font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify; background-color: rgb(255, 255, 255); color: rgb(51, 51, 51); font-size: 14px; font-weight: 700;\">PROFIL DINAS TENAGA KERJA SOSIAL DAN TRANSMIGRASI</span></div><span style=\"box-sizing: border-box; font-weight: 700; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><div style=\"text-align: center;\"><span style=\"font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">KABUPATEN MAGELANG</span></div></span><hr id=\"null\" style=\"box-sizing: content-box; height: 0px; margin-top: 20px; margin-bottom: 20px; border-right: 0px; border-bottom: 0px; border-left: 0px; border-image: initial; border-top-style: solid; border-top-color: rgb(238, 238, 238); color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-size: 14px; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify; background-color: rgb(255, 255, 255);\"><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"box-sizing: border-box; font-weight: 700; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">I.&nbsp; DASAR HUKUM</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"box-sizing: border-box; font-weight: 700; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><br style=\"box-sizing: border-box;\"></span></div><span style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-size: 14px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\">A.&nbsp; Dasar Hukum Berdirinya Organisasi :</span></span><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"></span></div><blockquote style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 0px 40px; font-size: 17.5px; border: none; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; background-color: rgb(255, 255, 255);\"><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;1.&nbsp; Undang-undang Nomor 14 tahun 1969 tentang ketentuan Pokok Ketenagakerjaan.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;2.&nbsp; Undang-undang nomor 7 tahun 1971 tentang ketentuan-ketentuan Pokok Ketransmigrasian.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;3.&nbsp; Undang-undang Nomor 11 Tahun 2009 tentang Kesejahteraan Sosial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;4.&nbsp; Peraturan Pemerintah Nomor 8 Tahun 2003 Tentang Pedoman Organisasi Perangkat Daerah.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;5.&nbsp; Peraturan Daerah Kabupaten Magelang Nomor 31 Tahun 2008 tentang Organisasi dan Tata Kerja Dinas Daerah.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;6.&nbsp; Peraturan Daerah Kabupaten Magelang Nomor 20 Tahun 2009 tentang Rincian Tugas Jabatan Struktural pada Dinas Tenaga Kerja Sosial dan Transmigrasi.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;7.&nbsp; Peraturan Daerah Nomor 11 Tahun 2011 Tentang Perubahan Atas Peraturan Daerah Kabupaten Magelang Nomor 33 Tahun 2008 Tentang Organisasi Dan Tata Kerja Lembaga Lain.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;8.&nbsp; Peraturan Bupati Nomor 19 Tahun 2011 Tentang Perubahan Atas Peraturan Bupati Magelang Nomor 1 Tahun 2009 Tentang Unit Pelaksana Teknis Badan Dan Dinas Di Lingkungan Pemerintah Kabupaten Magelang.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\"><br style=\"box-sizing: border-box;\"></span></div></blockquote><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><div style=\"box-sizing: border-box; text-align: center;\"><span style=\"box-sizing: border-box; font-weight: 700; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">II. Letak Geografis</span></div><div style=\"box-sizing: border-box; text-align: center;\"><span style=\"box-sizing: border-box; font-weight: 700; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><br style=\"box-sizing: border-box;\"></span></div><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: 700;\">A. Letak Geografis</span></span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"></span><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; Dinas Tenaga Kerja Sosial dan Transmigrasi Kabupaten Magelang, sampai saat ini masih bertempat di wilayah Kota Magelang, yaitu di Jalan Kartini Nomor 1B Magelang Telepon : (0293) 362423, Email :&nbsp;</span><a href=\"mailto:nakersostrans.kabmgl@gmail.com\" style=\"box-sizing: border-box; color: rgb(67, 105, 177); text-decoration-line: none; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">nakersostrans.kabmgl@gmail.com</a><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;</span><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">website :&nbsp;</span><a href=\"mailto:disnaker@magelangkab.go.id\" style=\"box-sizing: border-box; color: rgb(67, 105, 177); text-decoration-line: none; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">disnaker@magelangkab.go.id</a><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><div style=\"box-sizing: border-box; text-align: center;\"><span style=\"box-sizing: border-box; font-weight: 700; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">III. Tugas Pokok dan Fungsi</span></div><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box;\"><br style=\"box-sizing: border-box;\"></span></div><span class=\"Apple-tab-span\" style=\"box-sizing: border-box; white-space: pre;\">	</span>Tugas pokok dan fungsi organisasi Dinas Tenaga Kerja Sosial dan Transmigrasi adalah sebagai berikut :</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: 700;\">a. Tugas</span></span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"></span><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; Berdasar Peraturan Daerah Kabupaten Magelang Nomor 31 Tahun 2008 tentang Organisasi Dan Tata Kerja Dinas Daerah.&nbsp; Dinas Tenaga Kerja, Sosial dan Transmigrasi mempunyai tugas pokok ”Melaksanakan urusan pemerintahan daerah dibidang ketenagakerjaan, sosial dan ketransmigrasian berdasarkan azaz otonomi dan tugas pembantuan”.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: 700;\">b. Fungsi<br style=\"box-sizing: border-box;\"></span></span><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; Dinas Tenaga Kerja, Sosial dan Transmigrasi Kabupaten Magelang menurut Peraturan Bupati Magelang Nomor 20 Tahun 2009 tentang Rincian Tugas Jabatan Struktural pada Dinas Tenaga Kerja, Sosial dan Transmigrasi Kabupaten Magelang mempunyai fungsi&nbsp; sebagai berikut :</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; 1. Perumusan kebijakan teknis sesuai dengan lingkup tugasnya.</span><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; 2. Penyelenggaraan urusan pemerintahan dan pelayanan umum sesuai dengan lingkup tugasnya.</span><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; 3. Pembinaan dan Pelaksanaan tugas sesuai dengan lingkup tugasnya.</span><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">&nbsp;&nbsp; 4. Pelaksanaan tugas lain yang diberikan oleh Bupati sesuai dengan tugas dan fungsinya.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span class=\"Apple-tab-span\" style=\"box-sizing: border-box; white-space: pre;\">	</span>Aspek stratejik organisasi/peran organisasi dalam pelaksanaan pembangunan daerah dan penyelenggaraan pemerintahan.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span class=\"Apple-tab-span\" style=\"box-sizing: border-box; white-space: pre;\">	</span>Dinas Tenaga Kerja Sosial dan Transmigrasi Kabupaten Magelang mempunyai aspek stratejik atau peran yang sangat penting dalam pencapaian tujuan pembangunan masyarakat Kabupaten Magelang yang ”Semanah”, diantaranya :</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"></div><blockquote style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 0px 40px; font-size: 17.5px; border: none; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; background-color: rgb(255, 255, 255);\"><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">1. Pembinaan dan penempatan tenaga kerja.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">2. Pengawasan dan Perlindungan terhadap tenaga kerja.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">3. Pembinaan Hubungan industrial yang harmonis diantara pekerja dan pengusaha serta pemerintah (Tripartit).</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">4. Merehabilitasi kehidupan sosial masyarakat.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">5. Memberikan pelayanan sosial serta mengembangkan potensi sosial masyarakat.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">6. Mengatasi permasalahan penyebaran serta peningkatan kesejahteraan penduduk lewat program transmigrasi.</span></div></blockquote><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span class=\"Apple-tab-span\" style=\"box-sizing: border-box; white-space: pre;\">	</span>Dinas Tenaga Kerja Sosial dan Transmigrasi mempunyai tugas pokok melaksanakan urusan pemerintahan daerah di bidang ketenagakerjaan, sosial dan ketransmigrasian berdasarkan azas otonomi dan tugas pembantuan.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">A. Struktur Organisasi Dinas Tenaga Kerja Sosial dan Transmigrasi :</span><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"></div><blockquote style=\"box-sizing: border-box; padding: 0px; margin: 0px 0px 0px 40px; font-size: 17.5px; border: none; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; background-color: rgb(255, 255, 255);\"><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">1. Kepala</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">2. Sekretariat, terdiri dari :</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Subbagian Perencanaan Monitoring Evaluasi dan Pelaporan (Renmonevlap).</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Subbagian Keuangan.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; c. Subbagian Umum dan Kepegawaian.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">3. Bidang Hubungan Kerja dan Pengawasan Ketenagakerjaan (HKPTK) terdiri dari :</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Seksi Syarat Kerja dan Hubungan Industrial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Seksi Pengawasan dan Perlindungan Ketenagakerjaan.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">4. Bidang Pembinaan dan Penempatan Kerja, terdiri dari :</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Seksi Pembinaan Ketenagakerjaan.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Seksi Produktifitas Tenaga Kerja.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; c. Seksi Penempatan dan Perluasan Kerja.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">5. Bidang Transmigrasi, terdiri dari :</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Seksi Penyuluh Ketransmigrasian.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Seksi Penempatan Ketransmigrasian.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; c.Seksi Evaluasi dan Kerjasama Antar Daerah.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">6. Bidang Asistensi dan Rehabilitasi Sosial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Seksi Asistensi Sosial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Seksi Rehabilitasi Sosial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">7. Bidang Potensi dan Pelayanan Sosial</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Seksi Potensi Sosial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Seksi Pelayanan Sosial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">8. UPT Balai Latihan Kerja</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Kasubag Tata Usaha</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">9. Kelompok Jabatan Fungsional.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; a. Pengantar Kerja.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; b. Mediator Hubungan Industrial.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; c. Penggerak Swadaya Masyarakat.</span></div><div style=\"box-sizing: border-box;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; font-size: 14px; text-align: justify;\">&nbsp;&nbsp; d. Insruktur</span></div></blockquote><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">B. Sekretariat sebagaimana dimaksud, dipimpin oleh seorang Sekretaris Dinas yang berada dibawah dan bertanggung jawab kepada Kepala Dinas Tenaga Kerja Sosial dan Transmigrasi.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">C. Bagian Tata Usaha dan Bidang-bidang sebagaimana dimaksud, masing-masing dipimpin oleh seorang Kepala yang berada dibawah dan bertanggung jawab kepada Kepala Dinas Tenaga Kerja Sosial dan Transmigrasi.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">D. UPT Balai Latihan Kerja sebagaimana dimaksud dipimpin oleh seorang Kepala yang berada dibawah dan bertanggung jawab kepada Kepala Dinas Tenaga Kerja Sosial dan Transmigrasi.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">E. Sub-sub Bagian sebagaimana dimaksud, masing-masing dipimpin oleh seorang Kepala yang berada dibawah dan bertanggungjawab kepada Sekretaris Dinas.</span></div><div style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; padding: 0px; margin: 0px; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\"><span style=\"box-sizing: border-box; font-family: Verdana, &quot;Times New Roman&quot;, Times, serif; text-align: justify;\">F. Seksi-seksi sebagaimana dimaksud, masing-masing dipimpin oleh seorang Kepala yang berada dibawah dan bertanggung jawab kepada Kepala Bidang yang bersangkutan.</span></div>', '2017-03-04');

-- Table `slides` --
CREATE TABLE `slides` (
  `idSlides` int(11) NOT NULL AUTO_INCREMENT,
  `pathUrl` varchar(225) NOT NULL,
  `filename` varchar(255) NOT NULL,
  `judulSlides` varchar(100) NOT NULL,
  `deskripsi` text NOT NULL,
  `idAdmin` int(11) NOT NULL,
  `last_updated` date NOT NULL,
  PRIMARY KEY (`idSlides`),
  KEY `idAdmin` (`idAdmin`),
  CONSTRAINT `slides_ibfk_1` FOREIGN KEY (`idAdmin`) REFERENCES `admin` (`idAdmin`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=latin1;

