-- Table `staf_blk` --
CREATE TABLE `staf_blk` (
  `stafID` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(30) NOT NULL,
  `password` varchar(255) NOT NULL,
  `passnohash` varchar(255) NOT NULL,
  `nama` varchar(100) NOT NULL,
  `alamat` text NOT NULL,
  `telepon` varchar(30) NOT NULL,
  `photo` varchar(255) NOT NULL,
  `email` varchar(100) NOT NULL,
  `id_kejuruan` int(11) NOT NULL,
  `idAdmin` int(11) NOT NULL,
  `jabatan` enum('instruktur','pimpinan') NOT NULL,
  `created` date NOT NULL,
  `updated` date DEFAULT NULL,
  PRIMARY KEY (`stafID`),
  KEY `id_kejuruan` (`id_kejuruan`),
  KEY `idAdmin` (`idAdmin`),
  CONSTRAINT `staf_blk_ibfk_2` FOREIGN KEY (`id_kejuruan`) REFERENCES `kejuruan` (`id_kejuruan`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `staf_blk_ibfk_3` FOREIGN KEY (`idAdmin`) REFERENCES `admin` (`idAdmin`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;

