-- Table `visimisi` --
CREATE TABLE `visimisi` (
  `idVisiMisi` int(11) NOT NULL AUTO_INCREMENT,
  `text` text NOT NULL,
  `created` date NOT NULL,
  `updated` date DEFAULT NULL,
  `idAdmin` int(11) NOT NULL,
  PRIMARY KEY (`idVisiMisi`),
  KEY `idAdmin` (`idAdmin`),
  CONSTRAINT `visimisi_ibfk_1` FOREIGN KEY (`idAdmin`) REFERENCES `admin` (`idAdmin`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=latin1;

INSERT INTO `visimisi` (`idVisiMisi`, `text`, `created`, `updated`, `idAdmin`) VALUES
(1, 'Terwujudnya tenaga kerja, kehidupan sosial masyarakat dan transmigrasi yang sejahtera.\r\n<br>Mengembangkan informasi ketenagakerjaan dalam rangka memperluas kesempatan kerja dan berusaha.', '2017-03-03', '', 1),
(2, 'Meningkatkan etos kerja untuk mendorong terciptanya profesionalisme aparatur.<br>Meningkatakan penanganan Penyandang Masalah Kesejahteraan Sosial (PMKS) dan Potensi Sumber Kesejahteraan Sosial (PSKS).<br>Memfasilitasi pengerahan dan perpindahan calon transmigrasi yang terarah dan terpadu.', '2017-03-03', '', 1),
(3, 'Meningkatkan kompetensi calon tenaga kerja.<br>Meningkatkan perlindungan tenaga kerja dan hubungan industrial yang selaras, serasi dan seimbang.', '2017-03-03', '', 1);

